@import url('https://fonts.googleapis.com/css2?family=Lato:ital,wght@0,300;0,400;0,900;1,300;1,400;1,900&display=swap');


*,
*::before,
*::after {
    box-sizing: border-box;

}

html {
    scroll-behavior: smooth;
}

:root {

    --clr-icons: #FFFBFA;
    --clr-text: #fff;
    --clr-bg-dark: #051518;
    --clr-bg-light: #0E363F;
    --clr-accent: #FF9B0B;
    --main-fnt: 'Lato', sans-serif;

}


body {
    margin: 0 auto;
    font-family: var(--main-fnt);


    a {
        text-decoration: none;

    }
}

.geology__main-container {
    background-color: var(--clr-icons);
    width: 100%;
    height: 100vh;
    display: grid;
    grid-template-areas: 'header header header header'
                        'main main main main'
                        'footer footer footer footer'
    ;




    @media screen and (max-width: 800px) {
        display: flex;
        flex-flow: column nowrap;
        max-width: 800px;

    }
}

header {
    grid-area: header;
    width: 100%;
    display: flex;
    justify-content: space-between;
    height: 100px;
    align-items: center;
    position: fixed;
    z-index: 100;
    top: 0;
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: var(--clr-icons);
    box-shadow: 0 2px 5px 5px rgba(0, 0, 0, 0.069);

    .logo {
        margin-left: 10px;
        font-size: 3rem;
    }

    button {
        margin-right: 10px;
        width: 250px;
        border: none;
                    background-color: var(--clr-accent);
                    padding-top: 20px;
                    padding-bottom: 20px;
                    font-size: 1.5rem;
                    border-radius: 5rem;

                    transition: 500ms;



                    a {
                        color: #FFFBFA;
                    }


    }

    button:hover {
        box-shadow: 0 0 10px 10px rgba(0, 0, 0, 0.225);
        transform: translate(2px, -2px)
        }

    nav {
        font-size: 1.5rem;
        margin-left: 45%;

        a {
            text-decoration: none;
            padding: 10px;
            color: var(--clr-bg-light);

            transition: 500ms;
            z-index: 3;
            :visited {
                color:  black;
            }



        }






    }


    @media screen and (max-width: 800px) {

        width: 100%;
        font-size: 1rem;


    }

}


main {
    grid-area: main;
    width: 100%;
    display: grid;
    grid-template-areas: 
    'hero hero hero hero'
    'services services services services'
    'topics topics topics topics '
    'benefits benefits benefits benefits'
    'about about about about'
    ;

    margin-top: 25px;



        @media screen and (max-width: 800px){
            width: 100%;
            display: flex;
            flex-flow: column nowrap;

        }
}
/* Hero Box Style */
.geology__main-hero {

    display: grid;
    width:  100%;
    height: 100vh;
    grid-area: hero;
    overflow: hidden;

    grid-template-areas: 

    'info info images images'
    'info info images images'
    'stats stats images images'
    'stats stats images images'
    ;



    .hero-info {
            grid-area: info;
            width: 75vh;
            background-color: #FFFBFA;
            height: 70vh;
            display: flex;
            flex-flow: column nowrap; 
            padding: 50px;
            margin-left: 100px;

            h1 {
                margin-top: 130px;
                font-size: 5rem;
                width: 85%;

                @media screen and (max-width: 800px) {
                    margin-top: 200px;
                    margin: 0 auto;
                    width: 100%;


                }

                span    {
                    color:#FF9B0B;
                }
            }

            h2 {
                font-size: 2rem;
                width: 75%;
                color: rgb(124, 124, 124);

                @media screen and (max-width: 800px) {
                    padding-top: 50px;
                    margin: 0 auto;
                    width: 100%;
                    padding-bottom: 50px;


                }
            }

            .btn-div {
                padding-top: 100px;
                width: 100%;
                display: flex;
                flex-flow: row nowrap;
                justify-content: space-between;
                margin: 0 auto;
                align-items: center;
                margin-left: 100px;




                .explore-btn {
                    width: 250px;
                    border: none;
                    background-color: var(--clr-accent);
                    padding-top: 20px;
                    padding-bottom: 20px;
                    font-size: 1.5rem;
                    border-radius: 5rem;
                    margin-right: 50px;
                    transition: 500ms;
                    font-weight: 600;

                    a {
                        color: #FFFBFA;
                    }
                }

                .explore-btn:hover {
                    box-shadow: 0 0 10px 10px rgba(0, 0, 0, 0.225);
                    transform: translate(5px, -5px)
                }
                .play-btn {
                    display: flex;
                    align-items: center;
                    background-color: transparent;
                    width: 250px;
                    border: none;
                    padding-top: 10px;
                    padding-bottom: 10px;
                    font-size: 1.5rem;
                    font-weight: 600;
                    border-radius: 5rem;
                    margin-right: 50px;
                    transition: 500ms;

                    i {
                        padding: 10px;
                        color:#FF9B0B;
                        background-color: #0e363f4d;
                        width: 50px;
                        height: 50px;
                        border-radius: 50%;
                        font-size: 1.7rem;
                        transform: translateY(9px);

                    }

                    .border-circle {
                        left: 50%;
                        top: 50%;
                        width: 70px;
                        height: 70px;
                        border-radius: 50%;
                        border: 2px solid #FF9B0B;
                        background-color: transparent;
                        margin-right: 25px;



                    }



                }

                .play-btn:hover {
                    box-shadow: 0 0 10px 10px rgba(0, 0, 0, 0.225);
                    transform: translate(5px, -5px)




                }

                @media screen and (max-width: 500px) {
                    display: none;
                }


            }
            @media screen and (max-width: 800px) {
                display: flex;
                flex-flow: column nowrap;
                width: 100%;
                margin: 0 auto;



            }   
    }

    .color-box {
        grid-area: images;
        background-color: var(--clr-bg-light);
        width: 100%;
        height: 100vh;
        margin-top: 150px;
        position: relative;
        border-radius: 5rem 0 0 0;
        box-shadow: -5px 0px 5px 5px rgba(0, 0, 0, 0.118);

        @media screen and (max-width: 800px){
            display: none;
        }

    }

    .img-box {
        width: 35vh;
        grid-area: images;
        background-image: url(/images/pexels-oladimeji-ajegbile-2325729.jpg);
        z-index: 10;
        height: 75vh;
        width: 55vh;
        background-position: center;
        background-size: cover;
        margin: 50px;
        margin-top: 20vh;
        margin-bottom: 15vh;
        box-shadow: 6px 5px 10px 5px rgba(0, 0, 0, 0.202);
        border-radius: .5rem;

        @media screen and (max-width: 800px) {
            margin: 0 auto;
            width: 100%;
            z-index: 0;
            margin-top: 200px;
        }



    }

    @media screen and (max-width: 800px) {
        display: flex;
        flex-flow: column nowrap;
        width: 100%;
        height: auto;


    }

}


.stats-div {
    display: flex;
    flex-flow: row nowrap;
    grid-area: stats;
    justify-content: space-evenly;
    width: 100%;

    align-items: center;
    padding-bottom: 80px;
    align-items: center;


    @media screen and (max-width: 800px) {
        display: flex;
        flex-flow: column nowrap;
        width: 100%;
        margin: 0 auto;
        padding-bottom: 10px;


    }
}


.stats-item {

    width: 200px;
    height: 300px;
    display: flex;
    flex-direction: column nowrap;
    color: #051518;
    justify-content: center;
    align-items: center;

    strong {
        font-size: 3rem;
        padding: 25px;
    }

    p {
        font-size: 1.7rem;
        padding-bottom: 15px;

    }

    @media screen and (max-width: 800px) {
        display: flex;
        flex-flow: column nowrap;
        width: 100%;
        height: auto;


        strong {
            padding: 10px;
        }

        p{

            height: auto;
            padding: 5px;
        }

    }
}



/* End Hero Box Style */

/* Services Style */

.geology__main-services {

    width: 100%;
    height: auto;
    grid-area: services;
    background-color: white;
    display: flex;
    flex-flow: column nowrap;
    align-items: center;

        h3 {
            margin-top: 150px; 
            font-size: 4rem;
            color:#051518;
            font-weight: 600;
            text-transform: uppercase;

            span {
                color: #FF9B0B;
            }
        }

        .subheader {
            padding: 0;
            font-size: 2rem;
            width: 45%;
            color:rgb(124, 124, 124);
            text-align: center;

        }
        @media screen and (max-width: 800px) {
            display: flex;
            flex-flow: column nowrap;
            width: 100%;
            height: auto;

            h3 {
                margin-top: 50px;
                padding: 10px;
            }

            .subheader {
                width: 90%;
            }

        }

    }

    .geology__main-services-cardbox {
        display: flex;
        margin-top: 100px;
        width: 80%;



        .card {
            width: 25%;
            display: flex;
            flex-flow: column nowrap;
            margin: 0 auto;
            align-items: center;
            text-align: center;
            border: 1px solid lightblue;
            border-radius: .5rem;
            padding: 25px;  
            transition: 500ms;

            strong {
                font-weight: 400;
                font-size: 2.5rem;
                text-align: left;
                width: 100%;
                padding-top: 15px


            }

            p {
                font-size: 1.2rem;
                text-align: left;
                width: 100%;
            }

            i {
                font-size: 7rem;
                padding: 10px;
                color:#0E363F;

            }

        }

        .card:hover {
            box-shadow: 0 0 10px 10px rgba(0, 0, 0, 0.069);
            transform: scale(1.2)
        }


        @media screen and (max-width: 800px) {
            display: flex;
            flex-flow: column nowrap;
            width: 100%;



            .card { 
                width: 90%;
                height:auto;
                margin-bottom: 25px;
            }

        }
    }

    /*services style end */

     /* Topics style  */


     .geology__main-topics {
         grid-area: topics;
         width: 100%;
         height: auto;
         display: flex;
        flex-flow: column nowrap;
        align-items: center;
        background-color: #FFFBFA;
        justify-content: center;


            h4 {
            font-size: 4rem;
            text-transform: uppercase;
            align-self: flex-start;
            margin-left: 200px
            }

         .subheader {
            padding: 0;
            font-size: 2rem;
            width: 45%;
            color:rgb(124, 124, 124);


        }



        .topic-card-box {
            display: flex;
            width: 80%;
            flex-flow: row wrap;
            padding-bottom: 100px;
            justify-content: center;




            .topic-card {
                display: flex;
                flex-flow: column nowrap;
                border: 1.5px solid #FF9B0B;
                width: 45%;
                height: 70vh;
                margin: 15px;
                border-radius: .5rem;
                transition: 500ms;
                overflow: hidden;

                #topic-img {
                    background-position: center;
                    background-size: cover;
                    width: 100%;
                    height: 50%;
                    border-radius: .5rem .5rem 0rem 0rem;
                    padding: .5px;
                }

                    /*Topic Card Images */
                .img1 {
                    background-image: url(/images/img1.png);


                }


                .img2 {
                    background-image: url(/images/img2.png);

                }

                .img3 {
                    background-image: url(/images/img3.png);

                }

                .img4 {
                    background-image: url(/images/pexels-pixabay-73830.jpg);

                }

                .img5 {
                    background-image: url(/images/img5.png);

                }
                .img6 {
                    background-image: url(/images/img6.png);

                }
                .img7 {
                    background-image: url(/images/img6.png);

                }
                .img8 {
                    background-image: url(/images/img6.png);

                }
                .img9 {
                    background-image: url(/images/img6.png);

                }
                .img10 {
                    background-image: url(/images/img6.png);

                }

                    /* Topic Card Image End */

                #title {
                    font-size: 4rem;
                    text-align: left;
                    margin: 25px


                }

                #subtitle {
                    font-size: 1.7rem;
                    width: 70%;
                    margin: 25px;

                }

             #ch-btn {
                width: 250px;
                border: none;
                background-color: var(--clr-accent);
                padding-top: 15px;
                padding-bottom: 15px;
                font-size: 1.5rem;
                border-radius: 5rem;
                margin-right: 50px;
                margin-left: 60%;
                transition: 500ms;
                font-weight: 600;
                align-self: flex-end;
                margin-bottom: 25px;
             }

             #ch-btn:hover {
                box-shadow: 0 0 10px 10px rgba(0, 0, 0, 0.225);
                transform: translate(5px, 5px)
                }


            }

            .topic-card:hover {
                box-shadow: 0 0 10px 10px rgba(0, 0, 0, 0.10)
            }




        }

        @media screen and (max-width: 800px) {
            display: flex;
            flex-flow: column nowrap;
            width: 100%;
            margin: 0 auto;
            height: auto;


            h4 {
                margin: 0px;
                margin-left: 15px;

            }

            .topic-card-box {
                width: 100%;
                height: auto;
                margin: 0 auto;
                display: flex;
                flex-flow: column nowrap;
                justify-content: center;
                align-items: center;



                .topic-card {
                    width: 90%;
                    height: auto;



                }

                #topic-img {
                    width: 100%;
                    height: 30%;
                }


                #title {
                    font-size: 1rem;
                    text-align: left;
                    margin: 5px


                }

                #subtitle {
                    font-size: .7rem;
                    width: 70%;
                    margin: 5px;

                }
            }



        }

        .detail-btns {
            margin-bottom: 50px;
            display: flex;
            flex-flow: row nowrap;
        }
        #loadMoreprodDiv {
            background-color: #FF9B0B;
            color: white;
            border: none;
            padding-top: 10px;
            padding-bottom: 10px;
            width: 250px;
            border-radius: .5rem;
            margin: 10px;

        }

        #showlessprodDiv {
            background-color: #FF9B0B;
            color: white;
            border: none;
            padding-top: 10px;
            padding-bottom: 10px;
            width: 250px;
            border-radius: .5rem;
            margin: 10px;

        }
     }

     /* end topic styles */

     /* start tutor style*/

     .geology__main-tutor {
         display: grid;
         grid-template-areas: 'images images info info'
                    'images images info info';
         width: 100%;

         grid-area: benefits;
         background-color: var(--clr-bg-light);
         overflow: hidden;


         @media screen and (max-width: 800px) {
            display: flex;
            flex-flow: column nowrap;
            width: 100%;
            margin: 0 auto;
            height: auto;


            .tutor-img-container {
                margin: 0 auto;
                padding: 0;
                padding-top: 25px;



                img {
                    width: 90%;
                    height: 25%;
                    margin: 0 auto;

                }

                #tutor-img1 {
                    margin-left: 0;
                }

                #tutor-img2 {
                    margin-right: 0;
                }
                #tutor-img3 {
                    margin-left: 0;
                }
            }



         }


     }

    .tutor-img-container {
        display: flex;
        grid-area: images;
        flex-flow: column nowrap;
        width: 100%;
        margin-top: 25px;
        padding: 50px;
        margin-left: 50px;


        img {
            width: 350px;
            height: 350px;
            object-fit: cover;
            border: none;

        }

        #tutor-img1 {
            margin-left: 300px;
        }

        #tutor-img2 {
            margin-right: 100px;
        }
        #tutor-img3 {
            margin-left: 300px;
        }

    }



    .tutor-box {
        background-color: var(--clr-bg-dark);
        height: 65vh;
        width: 65vh;
        display: flex;
        flex-flow: column nowrap;
        margin-left: 20px;
        margin-top: 32vh;
        border-radius: .5rem;
        color: white;

        strong {
            font-size: 4.5rem;
            color: white;
            font-weight: 400;
            padding-left: 25px;
            margin-top: 55px;

        }

        p   {
            padding-left: 50px;
            font-size: 1.7rem;
            font-weight: 200;
            margin-top: 50px;
        }
        ul {
            padding-left: 50px;
            margin-left: 60px;
            list-style: none;

        }

        li {
            padding: 20px;
            padding-left: 25px;
            font-size: 1.6rem;


            i {
                padding-right: 20px;
                color: var(--clr-accent)
