    }

    .geology__main-about-box    {
        display: flex;
        justify-content: space-between;
        flex-flow: row nowrap;
        margin: 0;
    }

    h5 { 
        font-size: 5rem;
        margin-left: 200px;
        color: var(--clr-bg-dark);

        padding: 0px;

    }



    .about-info {
        display: flex;
        flex-flow: column nowrap;
        margin-top: 150px;


        .subheader {
            padding: 0;
            font-size: 2rem;
            width: 45%;
            color:rgb(124, 124, 124);
            margin-left: 225px;

